private static SpacingBuilder createSpacingBuilder (CodeStyleSettings settings)
{
  JetCodeStyleSettings jetSettings = settings.getCustomSettings(JetCodeStyleSettings.class);
  CommonCodeStyleSettings jetCommonSettings = settings.getCommonSettings(JetLanguage.INSTANCE);
  return new SpacingBuilder(settings).after(NAMESPACE_HEADER).blankLines(1).between(IMPORT_DIRECTIVE, IMPORT_DIRECTIVE).lineBreakInCode().after(IMPORT_LIST).blankLines(1).before(DOC_COMMENT).lineBreakInCode().before(FUN).lineBreakInCode().before(PROPERTY).lineBreakInCode().between(FUN, FUN).blankLines(1).between(FUN, PROPERTY).blankLines(1).afterInside(LBRACE, BLOCK).lineBreakInCode().beforeInside(RBRACE, CLASS_BODY).lineBreakInCode().beforeInside(RBRACE, BLOCK).lineBreakInCode().before(COMMA).spaceIf(jetCommonSettings.SPACE_BEFORE_COMMA).after(COMMA).spaceIf(jetCommonSettings.SPACE_AFTER_COMMA).around(TokenSet.create(EQ, MULTEQ, DIVEQ, PLUSEQ, MINUSEQ, PERCEQ)).spaceIf(jetCommonSettings.SPACE_AROUND_ASSIGNMENT_OPERATORS).around(TokenSet.create(ANDAND, OROR)).spaceIf(jetCommonSettings.SPACE_AROUND_LOGICAL_OPERATORS).around(TokenSet.create(EQEQ, EXCLEQ, EQEQEQ, EXCLEQEQEQ)).spaceIf(jetCommonSettings.SPACE_AROUND_EQUALITY_OPERATORS).aroundInside(TokenSet.create(LT, GT, LTEQ, GTEQ), BINARY_EXPRESSION).spaceIf(jetCommonSettings.SPACE_AROUND_RELATIONAL_OPERATORS).aroundInside(TokenSet.create(PLUS, MINUS), BINARY_EXPRESSION).spaceIf(jetCommonSettings.SPACE_AROUND_ADDITIVE_OPERATORS).aroundInside(TokenSet.create(MUL, DIV, PERC), BINARY_EXPRESSION).spaceIf(jetCommonSettings.SPACE_AROUND_MULTIPLICATIVE_OPERATORS).around(TokenSet.create(PLUSPLUS, MINUSMINUS, EXCLEXCL, MINUS, PLUS, EXCL)).spaceIf(jetCommonSettings.SPACE_AROUND_UNARY_OPERATOR).around(RANGE).spaceIf(jetSettings.SPACE_AROUND_RANGE).beforeInside(BLOCK, FUN).spaceIf(jetCommonSettings.SPACE_BEFORE_METHOD_LBRACE).afterInside(LPAR, VALUE_PARAMETER_LIST).spaces(0).beforeInside(RPAR, VALUE_PARAMETER_LIST).spaces(0).afterInside(LT, TYPE_PARAMETER_LIST).spaces(0).beforeInside(GT, TYPE_PARAMETER_LIST).spaces(0).afterInside(LPAR, VALUE_ARGUMENT_LIST).spaces(0).beforeInside(RPAR, VALUE_ARGUMENT_LIST).spaces(0).afterInside(LT, TYPE_ARGUMENT_LIST).spaces(0).beforeInside(GT, TYPE_ARGUMENT_LIST).spaces(0).betweenInside(FOR_KEYWORD, LPAR, FOR).spacing(1, 1, 0, false, 0).betweenInside(IF_KEYWORD, LPAR, IF).spacing(1, 1, 0, false, 0).betweenInside(WHILE_KEYWORD, LPAR, WHILE).spacing(1, 1, 0, false, 0).betweenInside(WHILE_KEYWORD, LPAR, DO_WHILE).spacing(1, 1, 0, false, 0).aroundInside(WHILE_KEYWORD, DO_WHILE).spaces(1).afterInside(DO_KEYWORD, DO_WHILE).spaces(1).beforeInside(COLON, PROPERTY).spaceIf(jetSettings.SPACE_BEFORE_TYPE_COLON).afterInside(COLON, PROPERTY).spaceIf(jetSettings.SPACE_AFTER_TYPE_COLON).beforeInside(COLON, FUN).spaceIf(jetSettings.SPACE_BEFORE_TYPE_COLON).afterInside(COLON, FUN).spaceIf(jetSettings.SPACE_AFTER_TYPE_COLON).beforeInside(COLON, VALUE_PARAMETER).spaceIf(jetSettings.SPACE_BEFORE_TYPE_COLON).afterInside(COLON, VALUE_PARAMETER).spaceIf(jetSettings.SPACE_AFTER_TYPE_COLON).beforeInside(COLON, TYPE_CONSTRAINT).spaceIf(jetSettings.SPACE_BEFORE_EXTEND_COLON).afterInside(COLON, TYPE_CONSTRAINT).spaceIf(jetSettings.SPACE_AFTER_EXTEND_COLON).beforeInside(COLON, CLASS).spaceIf(jetSettings.SPACE_BEFORE_EXTEND_COLON).afterInside(COLON, CLASS).spaceIf(jetSettings.SPACE_AFTER_EXTEND_COLON).beforeInside(COLON, TYPE_PARAMETER).spaceIf(jetSettings.SPACE_BEFORE_EXTEND_COLON).afterInside(COLON, TYPE_PARAMETER).spaceIf(jetSettings.SPACE_AFTER_EXTEND_COLON).between(VALUE_ARGUMENT_LIST, FUNCTION_LITERAL_EXPRESSION).spaces(1).beforeInside(ARROW, FUNCTION_LITERAL).spaceIf(jetSettings.SPACE_BEFORE_LAMBDA_ARROW).aroundInside(ARROW, WHEN_ENTRY).spaceIf(jetSettings.SPACE_AROUND_WHEN_ARROW).beforeInside(LBRACE, WHEN).spacing(1, 1, 0, true, 0).aroundInside(ARROW, FUNCTION_TYPE).spaceIf(jetSettings.SPACE_AROUND_FUNCTION_TYPE_ARROW).betweenInside(REFERENCE_EXPRESSION, FUNCTION_LITERAL_EXPRESSION, CALL_EXPRESSION).spaces(1).aroundInside(ELSE_KEYWORD, IF).spaces(1).betweenInside(RPAR, THEN, IF).spaces(1).between(RPAR, BODY).spaces(1);
  return;
}